import React, { Fragment, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import Navbar from '../../layouts/Navbar';
import Container from '../../layouts/Container';
import Wrapper from '../../layouts/Wrapper';

import style from './SellerAdd.module.css';

import MultiSelectBox from '../../layouts/features/MultiSelectBox';
import SelectBox from '../../layouts/features/SelectBox';

import attach_icon from '../../assets/images/attach_icon.png';
import calender_icon from '../../assets/images/calendar_icon.png';
import location_icon from '../../assets/images/location_icon.png';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

 const SellerAdd = (props) => {

    const {
        register,
        handleSubmit,
        errors,
        setValue,
        getValues,
        control,
      } = useForm({
        mode: 'onBlur',
      });
    
    const onSubmit = (data) => {
        console.log(data);
      };

      const onFileUpload = () => {
        console.log(getValues('license_attachment'));
      };
      const CustomDatePickerInputForVAT = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='VAT reg. expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForLicense = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='License expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForCR = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='CR expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForLocation = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='Set your location:'
              />
    
              <img
                className={style.location_input_icon}
                src={location_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
    
     return(
         <Fragment>
             <Navbar/>
             <Container>
                 <Wrapper>
                 <div className={style.container}>
                 <form className={style.form_wrapper} onSubmit={handleSubmit(onSubmit)}>
                      {/*
           */}
          <div className={style.form_group}>
            <input
              className={style.input_box_disabled}
              name='company_code'
              placeholder='Company code:'
              disabled={true}
              ref={register({ required: true })}
            />

            {errors.company_code && (
              <span className={style.error}>Company code is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='company_name'
              placeholder='Company name:'
              ref={register({ required: true })}
            />

            {errors.company_name && (
              <span className={style.error}>Company name is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box_disabled}
              name='branch_code'
              placeholder='Branch code:'
              disabled={true}
              ref={register({ required: true })}
            />

            {errors.branch_code && (
              <span className={style.error}>Branch code is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='branch_name'
              placeholder='Branch name:'
              ref={register({ required: true })}
            />

            {errors.branch_name && (
              <span className={style.error}>Branch name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <MultiSelectBox
              items={[
                { id: 1, label: 'Select Business Line:', value: '' },
                { id: 2, label: 'Pharmacy', value: 'Pharmacy' },
                { id: 3, label: 'Hospital', value: 'Hospital' },
                { id: 4, label: 'Medical Center', value: 'Medical Center' },
              ]}
              name='business_line'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.business_line && (
              <span className={style.error}>Business Line is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='country'
              placeholder='Country:'
              ref={register({ required: true })}
            />

            {errors.country && (
              <span className={style.error}>Country name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='region'
              placeholder='Region:'
              ref={register({ required: true })}
            />

            {errors.region && (
              <span className={style.error}>Region  is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='city'
              placeholder='City:'
              ref={register({ required: true })}
            />

            {errors.city && (
              <span className={style.error}>City is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Authorised_contact_person'
              placeholder='Authorised contact person:'
              ref={register({ required: true })}
            />

            {errors.Authorised_contact_person && (
              <span className={style.error}>Authorised contact person name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Telephone_no'
              placeholder='Telephone no:'
              ref={register({ required: true })}
            />

            {errors.Telephone_no && (
              <span className={style.error}>Telephone no is required</span>
            )}
          </div>
