import React, { Fragment, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import Navbar from '../../layouts/Navbar';
import Container from '../../layouts/Container';
import Wrapper from '../../layouts/Wrapper';

import style from './SellerAdd.module.css';

import MultiSelectBox from '../../layouts/features/MultiSelectBox';
import SelectBox from '../../layouts/features/SelectBox';

import attach_icon from '../../assets/images/attach_icon.png';
import calender_icon from '../../assets/images/calendar_icon.png';
import location_icon from '../../assets/images/location_icon.png';
import medic_logo from '../../assets/images/medic_logo.png';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

 const SellerAdd = (props) => {

    const {
        register,
        handleSubmit,
        errors,
        setValue,
        getValues,
        control,
      } = useForm({
        mode: 'onBlur',
      });
    
    const onSubmit = (data) => {
        console.log(data);
      };

      const onFileUpload = () => {
        console.log(getValues('license_attachment'));
      };
      const CustomDatePickerInputForVAT = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='VAT reg. expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForLicense = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='License expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForCR = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='CR expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
      const CustomDatePickerInputForLocation = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='Set your location:'
              />
    
              <img
                className={style.location_input_icon}
                src={location_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
    
     return(
         <Fragment>
             <Navbar/>
             <Container>
                 <Wrapper>
                 <div className={style.container}>
                 <img className={style.medic_logo} src={medic_logo} alt='' />{' '}
                 <form className={style.form_wrapper} onSubmit={handleSubmit(onSubmit)}>
                      {/*
           */}
          <div className={style.form_group}>
            <input
              className={style.input_box_disabled}
              name='company_code'
              placeholder='Company code:'
              disabled={true}
              ref={register({ required: true })}
            />

            {errors.company_code && (
              <span className={style.error}>Company code is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
