import React, { Fragment, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import Navbar from '../../layouts/Navbar';
import Container from '../../layouts/Container';
import Wrapper from '../../layouts/Wrapper';

import style from './SellerAdd.module.css';

import MultiSelectBox from '../../layouts/features/MultiSelectBox';
import SelectBox from '../../layouts/features/SelectBox';

const SellerAdd_Company = (props) => {
    const pageTitle = 'New / edit seller (Company)';
    const {
        register,
        handleSubmit,
        errors,
        setValue,
        getValues,
        control,
      } = useForm({
        mode: 'onBlur',
      });
    
    const onSubmit = (data) => {
        console.log(data);
      };

    return(
    <Fragment>
         <Navbar pageTitle={pageTitle} />
         <Container>
             <Wrapper>
             <div className={style.container}>
             <form className={style.form_wrapper} onSubmit={handleSubmit(onSubmit)}>
                      {/*
           */}
          <div className={style.form_group}>
            <input
              className={style.input_box_disabled}
              name='company_code'
              placeholder='Company code:'
              disabled={true}
              ref={register({ required: true })}
            />

            {errors.company_code && (
              <span className={style.error}>Company code is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='company_name'
              placeholder='Company name:'
              ref={register({ required: true })}
            />

            {errors.company_name && (
              <span className={style.error}>Company name is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <MultiSelectBox
              items={[
                { id: 1, label: 'Select Business Line:', value: '' },
                { id: 2, label: 'Pharmacy', value: 'Pharmacy' },
                { id: 3, label: 'Hospital', value: 'Hospital' },
                { id: 4, label: 'Medical Center', value: 'Medical Center' },
              ]}
              name='business_line'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.business_line && (
              <span className={style.error}>Business Line is required</span>
            )}
          </div>
          {/*
           */}
             <div className={style.form_group}>
            <input
              className={style.input_box}
              name='user_id_or_email'
              placeholder='User ID/email:'
              ref={register({ required: true })}
            />

            {errors.user_id_or_email && (
              <span className={style.error}>User ID/email is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Authorised_contact_person'
              placeholder='Authorised contact person:'
              ref={register({ required: true })}
            />

            {errors.Authorised_contact_person && (
              <span className={style.error}>Authorised contact person name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Telephone_no'
              placeholder='Telephone no:'
              ref={register({ required: true })}
            />

            {errors.Telephone_no && (
              <span className={style.error}>Telephone no is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='mobile_no'
              placeholder='Mobile no:'
              ref={register({ required: true })}
            />

            {errors.mobile_no && (
              <span className={style.error}>Mobile no is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group_horizontal}>
                <div className={style.form_group_checkbox}>
                  <label
                    className={style.checkbox_container}
                    htmlFor='Active'
                    tabIndex='0'
                    onClick={() => onClickCheckbox('Active')}
                    onKeyDown={(e) =>
                      e.keyCode === 32 ? onClickCheckbox('Active') : ''
                    }
                  >
                    VAT excluded
              <input name='Active' type='checkbox' ref={register} />
                    <span className={style.checkbox}></span>
                  </label>

                  {errors.vat_excluded && (
                    <span className={style.error}>VAT Excluded is required</span>
                  )}
                </div>
                <div className={style.form_group_small}>
                  <SelectBox
                    items={[
                      { id: 1, label: 'VAT percentage:', value: '' },
                      { id: 2, label: '100', value: 1 },
                      { id: 3, label: '50', value: 2 },
                      { id: 4, label: '20', value: 3 },
                    ]}
                    name='VAT_percentage'
                    setValue={setValue}
                    inputRef={register({ required: true })}
                  />
                  {errors.VAT_percentage && (
                    <span className={style.error}>
                      VAT percentage is required
                    </span>
                  )}
                </div>
              </div>
              {/*
         */}
           
           
           </form>
             </div> 
             </Wrapper>
         </Container>
    </Fragment>
    )
}
export default SellerAdd_Company;
