import React, { Fragment, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import Navbar from '../../layouts/Navbar';
import Container from '../../layouts/Container';
import Wrapper from '../../layouts/Wrapper';

import style from './SellerAdd.module.css';

import MultiSelectBox from '../../layouts/features/MultiSelectBox';
import SelectBox from '../../layouts/features/SelectBox';

import attach_icon from '../../assets/images/attach_icon.png';
import calender_icon from '../../assets/images/calendar_icon.png';
import location_icon from '../../assets/images/location_icon.png';
import medic_logo from '../../assets/images/medic_logo.png';
import download_icon from '../../assets/images/download_icon_light_blue.png';
import upload_icon from '../../assets/images/download_icon.png';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const SellerAdd_Company = (props) => {
    const pageTitle = 'New / edit seller (Company)';
    const {
        register,
        handleSubmit,
        errors,
        setValue,
        getValues,
        control,
      } = useForm({
        mode: 'onBlur',
      });
    
    const onSubmit = (data) => {
        console.log(data);
      };
      const [checkboxSelected, setCheckboxSelected] = useState({
        Active: false,
      });
      const onClickCheckbox = (name) => {
        setValue(name, !getValues(name), {
          shouldValidate: true,
        });
        setCheckboxSelected({ ...checkboxSelected, [name]: getValues(name) });
      };
      const onFileUpload = () => {
        console.log(getValues('license_attachment'));
      };
      const CustomDatePickerInputForLicense = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='License expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
     
    return(
    <Fragment>
         <Navbar pageTitle={pageTitle} />
         <Container>
             <Wrapper>
             <div className={style.container}>
                 <p className={style.title}>Company profile</p>
             <form className={style.form_wrapper} onSubmit={handleSubmit(onSubmit)}>
                      {/*
           */}
          <div className={style.form_group}>
            <input
              className={style.input_box_disabled}
              name='company_code'
              placeholder='Company code:'
              disabled={true}
              ref={register({ required: true })}
            />

            {errors.company_code && (
              <span className={style.error}>Company code is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='company_name'
              placeholder='Company name:'
              ref={register({ required: true })}
            />

            {errors.company_name && (
              <span className={style.error}>Company name is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <MultiSelectBox
              items={[
                { id: 1, label: 'Select Business Line:', value: '' },
                { id: 2, label: 'Pharmacy', value: 'Pharmacy' },
                { id: 3, label: 'Hospital', value: 'Hospital' },
                { id: 4, label: 'Medical Center', value: 'Medical Center' },
              ]}
              name='business_line'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.business_line && (
              <span className={style.error}>Business Line is required</span>
            )}
          </div>
          {/*
           */}
             <div className={style.form_group}>
            <input
              className={style.input_box}
              name='user_id_or_email'
              placeholder='User ID/email:'
              ref={register({ required: true })}
            />

            {errors.user_id_or_email && (
              <span className={style.error}>User ID/email is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Authorised_contact_person'
              placeholder='Authorised contact person:'
              ref={register({ required: true })}
            />

            {errors.Authorised_contact_person && (
              <span className={style.error}>Authorised contact person name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='Telephone_no'
              placeholder='Telephone no:'
              ref={register({ required: true })}
            />

            {errors.Telephone_no && (
              <span className={style.error}>Telephone no is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='mobile_no'
              placeholder='Mobile no:'
              ref={register({ required: true })}
            />

            {errors.mobile_no && (
              <span className={style.error}>Mobile no is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group_horizontal}>
                <div className={style.form_group_checkbox}>
                  <label
                    className={style.checkbox_container}
                    htmlFor='Active'
                    tabIndex='0'
                    onClick={() => onClickCheckbox('Active')}
                    onKeyDown={(e) =>
                      e.keyCode === 32 ? onClickCheckbox('Active') : ''
                    }
                  >
                    VAT excluded
              <input name='Active' type='checkbox' ref={register} />
                    <span className={style.checkbox}></span>
                  </label>

                  {errors.vat_excluded && (
                    <span className={style.error}>VAT Excluded is required</span>
                  )}
                </div>
                <div className={style.form_group_small}>
                  <SelectBox
                    items={[
                      { id: 1, label: 'VAT percentage:', value: '' },
                      { id: 2, label: '100', value: 1 },
                      { id: 3, label: '50', value: 2 },
                      { id: 4, label: '20', value: 3 },
                    ]}
                    name='VAT_percentage'
                    setValue={setValue}
                    inputRef={register({ required: true })}
                  />
                  {errors.VAT_percentage && (
                    <span className={style.error}>
                      VAT percentage is required
                    </span>
                  )}
                </div>
              </div>
              {/*
         */}
            <div className={style.form_group}>
            <SelectBox
              items={[
                { id: 1, label: 'Commission %:', value: '' },
                { id: 2, label: '10%', value: 1 },
                { id: 3, label: '20%', value: 2 },
                { id: 4, label: '30%', value: 3 },
              ]}
              name='commission'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.commission && (
              <span className={style.error}>commission% is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <SelectBox
              items={[
                { id: 1, label: 'Commission % to salesman:', value: '' },
                { id: 2, label: '10%', value: 1 },
                { id: 3, label: '20%', value: 2 },
                { id: 4, label: '30%', value: 3 },
              ]}
              name='commission_to_salesman'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.commission_to_salesman && (
              <span className={style.error}>commission % to salesman is required</span>
            )}
          </div>
          {/*
           */}
             <div className={style.form_group}>
            <input
              className={style.input_box}
              name='license_no'
              placeholder='License no:'
              ref={register({ required: true })}
            />

            {errors.license_no && (
              <span className={style.error}>License no is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group_horizontal_halved}>
            <div className={style.form_group_halved}>
              <Controller
                control={control}
                name='license_exp_date'
                defaultValue=''
                rules={{ required: true }}
                render={({ onChange, onBlur, value }) => (
                  <DatePicker
                    onChange={onChange}
                    onBlur={onBlur}
                    selected={value}
                    minDate={new Date()}
                    // showYearDropdown
                    // scrollableYearDropdown
                    // yearDropdownItemNumber={30}
                    onChangeRaw={(e) => e.preventDefault()}
                    customInput={<CustomDatePickerInputForLicense/>}
                  />
                )}
              />
              {errors.license_exp_date && (
                <span className={style.error}>
                  License expiry date is required
                </span>
              )}
            </div>
            <div className={style.form_group_halved}>
              <div className={style.file_input}>
                <label
                  className={`${style.input_box_half_right} ${style.file_input_label}`}
                  htmlFor='attachment'
                  onClick={() => onFileUpload}
                >
                  Attachment
                </label>
                <img
                  src={attach_icon}
                  className={style.file_input_icon}
                  alt=''
                  htmlFor='attachment'
                />
                <input
                  style={{ display: 'none' }}
                  name='attachment'
                  id='attachment'
                  placeholder='Attac'
                  type='file'
                  ref={register({ required: true })}
                />
              </div>

              {errors.license_attachment && (
                <span className={style.error}>Attachment is required</span>
              )}
            </div>
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <SelectBox
              items={[
                { id: 1, label: 'Bank transaction at corporate level:', value: '' },
                { id: 2, label: '10%', value: 1 },
                { id: 3, label: '20%', value: 2 },
                { id: 4, label: '30%', value: 3 },
              ]}
              name='Bank_transaction_at_corporate_level'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.Bank_transaction_at_corporate_level && (
              <span className={style.error}>Bank transaction at corporate level is required</span>
            )}
          </div>
          {/*
           */}
           
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='IBAN_no'
              placeholder='IBAN no:'
              ref={register({ required: true })}
            />

            {errors.IBAN_no && (
              <span className={style.error}>IBAN NO is required</span>
            )}
          </div>
          {/*
           */}
             <div className={style.form_group}>
            <input
              className={style.input_box}
              name='bank_name'
              placeholder='Bank name:'
              ref={register({ required: true })}
            />

            {errors.bank_name && (
              <span className={style.error}>Bank name is required</span>
            )}
          </div>
          {/*
           */}
             <div className={style.form_group}>
            <input
              className={style.input_box}
              name='beneficiary_name'
              placeholder='Beneficiary name:'
              ref={register({ required: true })}
            />

            {errors.beneficiary_name && (
              <span className={style.error}>Beneficiary name is required</span>
            )}
          </div>
            {/*
           */}
           <p className={style.title}>Geographical coverage </p>
           <div className={style.form_group_horizontal}>
                <div className={style.form_group_checkbox}>
                 <p className={style.subtitle}>Number of cities:</p>
                </div>
                <div className={style.form_group_small}>
                  <SelectBox
                    items={[
                      { id: 1, label: '-2-', value: '' },
                      { id: 2, label: '-3-', value: 1 },
                      { id: 3, label: '-4-', value: 2 },
                      { id: 4, label: '-5-', value: 3 },
                    ]}
                    name='no_of_cities'
                    setValue={setValue}
                    inputRef={register({ required: true })}
                  />
                  {errors.no_of_cities && (
                    <span className={style.error}>
                      No of cities is required
                    </span>
                  )}
                </div>
              </div>
              {/*
         */}
          <div className={style.form_group}>
            <input
              className={style.input_box}
              name='country'
              placeholder='Country:'
              ref={register({ required: true })}
            />

            {errors.country && (
              <span className={style.error}>Country name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='region'
              placeholder='Region:'
              ref={register({ required: true })}
            />

            {errors.region && (
              <span className={style.error}>Region  is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='city'
              placeholder='City:'
              ref={register({ required: true })}
            />

            {errors.city && (
              <span className={style.error}>City is required</span>
            )}
          </div>
          {/*
           */}
           <div style={{height:'40px'}}/>
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='country'
              placeholder='Country:'
              ref={register({ required: true })}
            />

            {errors.country && (
              <span className={style.error}>Country name is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='region'
              placeholder='Region:'
              ref={register({ required: true })}
            />

            {errors.region && (
              <span className={style.error}>Region  is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='city'
              placeholder='City:'
              ref={register({ required: true })}
            />

            {errors.city && (
              <span className={style.error}>City is required</span>
            )}
          </div>
          {/*
           */}
           
          <div className={style.form_group_horizontal_halved}>
            <div className={style.form_group_halved}>
              <Controller
                control={control}
                name=''
                defaultValue=''
                rules={{ required: true }}
                render={() => (
                    <div>
                    <label
                    className={`${style.input_box_half_left} ${style.file_input_label}`}
                    htmlFor='Contract_upload'
                    onClick={() => onFileUpload}
                  >
                Contract upload
                <img
                  src={upload_icon}
                  //className={style.file_input_icon}
                  style={{width:'20px',float:'right'}}
                  alt=''
                  htmlFor='Contract_upload'
                />
                  </label>
                  </div>  
                )}
              />
              {errors.Contract_upload && (
                <span className={style.error}>Contract upload is required</span>
              )}
            </div>
            <div className={style.form_group_halved}>
              <div className={style.file_input}>
              <label
                  className={`${style.input_box_half_right} ${style.file_input_label}`}
                  htmlFor='download'
                  onClick={() => onFileUpload}
                >
                 Download                
                </label>
                <img
                  src={download_icon}
                  className={style.file_input_icon}
                  alt=''
                  htmlFor='download'
                />
                <input
                  style={{ display: 'none' }}
                  name='attachment'
                  id='attachment'
                  placeholder='Attac'
                  type='file'
                  ref={register({ required: true })}
                />
              </div>

              {errors.license_attachment && (
                <span className={style.error}>Attachment is required</span>
              )}
              </div>
            </div>
          {/*
           */}
             <input className={style.save_btn} type='submit' value='SAVE' />
           </form>
             </div> 
             </Wrapper>
         </Container>
    </Fragment>
    )
}
export default SellerAdd_Company;
