import React, { Fragment, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import Navbar from '../../layouts/Navbar';
import Container from '../../layouts/Container';
import Wrapper from '../../layouts/Wrapper';

import style from './ExpensesAdd.module.css';
import calender_icon from '../../assets/images/calendar_icon.png';

import SelectBox from '../../layouts/features/SelectBox';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
const ExpensesAdd = (props) => {
    const pageTitle = 'New / edit expenses';
    const {
        register,
        handleSubmit,
        errors,
        setValue,
        getValues,
        control,
      } = useForm({
        mode: 'onBlur',
      });
    const onSubmit = (data) => {
        console.log(data);
      };
      const CustomDatePickerInputForLicense = React.forwardRef(
        ({ onClick, onChange, value }, ref) => {
          return (
            <Fragment>
              <input
                className={style.date_picker_input}
                onClick={onClick}
                onChange={onChange}
                value={value}
                ref={ref}
                placeholder='License expiry date:'
              />
    
              <img
                className={style.date_picker_input_icon}
                src={calender_icon}
                alt=''
                onClick={onClick}
              />
            </Fragment>
          );
        }
      );
    return(
        <Fragment>
             <Navbar pageTitle={pageTitle} />
            <Container>
                <Wrapper>
                <div className={style.container}>
             <form className={style.form_wrapper} onSubmit={handleSubmit(onSubmit)}>
                      {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='ref_no'
              placeholder='Ref no:'
              ref={register({ required: true })}
            />

            {errors.ref_no && (
              <span className={style.error}>Ref no is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
              <Controller
                control={control}
                name='license_exp_date'
                defaultValue=''
                rules={{ required: true }}
                render={({ onChange, onBlur, value }) => (
                  <DatePicker
                    onChange={onChange}
                    onBlur={onBlur}
                    selected={value}
                    minDate={new Date()}
                    // showYearDropdown
                    // scrollableYearDropdown
                    // yearDropdownItemNumber={30}
                    onChangeRaw={(e) => e.preventDefault()}
                    customInput={<CustomDatePickerInputForLicense/>}
                  />
                )}
              />
              {errors.license_exp_date && (
                <span className={style.error}>
                  License expiry date is required
                </span>
              )}
            </div>
          {/*
           */}
            <div className={style.form_group}>
            <input
              className={style.input_box}
              name='description'
              placeholder='Description:'
              ref={register({ required: true })}
            />

            {errors.description && (
              <span className={style.error}>Description is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <SelectBox
              items={[
                { id: 1, label: 'Type:', value: '' },
                { id: 2, label: 'a', value: 1 },
                { id: 3, label: 'b', value: 2 },
                { id: 4, label: 'c', value: 3 },
              ]}
              name='type'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.type && (
              <span className={style.error}>Type is required</span>
            )}
          </div>
          {/*
           */}
            <div className={style.form_group}>
            <SelectBox
              items={[
                { id: 1, label: 'Catagory:', value: '' },
                { id: 2, label: 'a', value: 1 },
                { id: 3, label: 'b', value: 2 },
                { id: 4, label: 'c', value: 3 },
              ]}
              name='catagory'
              setValue={setValue}
              inputRef={register({ required: true })}
            />
            {errors.catagory && (
              <span className={style.error}>Catagory is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='amount'
              placeholder='Amount:'
              ref={register({ required: true })}
            />

            {errors.amount && (
              <span className={style.error}>Amount is required</span>
            )}
          </div>
          {/*
           */}
           <div className={style.form_group}>
            <input
              className={style.input_box}
              name='remarks'
              placeholder='Remarks:'
              ref={register({ required: true })}
            />

            {errors.remarks && (
              <span className={style.error}>Remarks is required</span>
            )}
          </div>
          {/*
           */}
           <input className={style.save_btn} type='submit' value='SAVE' />
           </form>
           </div>
                </Wrapper>
            </Container>
        </Fragment>
    )
}

export default ExpensesAdd;
