import React, { useRef, useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
// import { connect } from 'react-redux';
// import PropTypes from 'prop-types';

import style from './Menu.module.css';

import back_btn from '../../../assets/images/back_btn.png';

// import { setMenuOpen } from '../../../actions/menuActions';

const Menu = ({ setMenuOpen }) => {
  const wrapperRef = useRef(null);
  const [openedMenu, setOpenedMenu] = useState('');
  const [backBtnClicked, setBackBtnClicked] = useState(false);

  useEffect(() => {
    document.addEventListener('click', handleClickOutside, false);
    return () => {
      document.removeEventListener('click', handleClickOutside, false);
    };
    // eslint-disable-next-line
  }, []);

  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setMenuOpen(false);
    }
  };

  const handleClickOnBackBtn = () => {
    setBackBtnClicked(true);
  };

  const handleClickOnNavBtn = () => {
    setMenuOpen(false);
  };

  useEffect(() => {
    setOpenedMenu('');
  }, [backBtnClicked]);

  useEffect(() => {
    setBackBtnClicked(false);
  }, [openedMenu]);

  return (
    <div className={style.menu_container} ref={wrapperRef}>
      <div
        className={
          openedMenu === ''
            ? style.main_menu_items
            : style.main_menu_items_hidden
        }
      >
        <NavLink
          onClick={handleClickOnNavBtn}
          to='/'
          className={style.main_menu_items_link}
          //   activeClassName={style.main_menu_items_active}
        >
          <div>Dashboard</div>
        </NavLink>
        <span className={style.divider}></span>
        <div onClick={() => setOpenedMenu('masters')}>Masters & Settings </div>
        <span className={style.divider}></span>
        <div onClick={() => setOpenedMenu('reports')}>Reports</div>
        <span className={style.divider}></span>
        <NavLink
          onClick={handleClickOnNavBtn}
          to='/login'
          className={style.main_menu_items_link}
          activeClassName={style.main_menu_items_active}
        >
          <div>Sign Out</div>
        </NavLink>
      </div>

      {openedMenu === 'masters' ? (
        <div className={style.sub_menu}>
          <div className={style.sub_menu_header_container}>
            <div className={style.sub_menu_header}>Masters & Settings</div>
            <div className={style.back_btn}>
              <img onClick={handleClickOnBackBtn} src={back_btn} alt='' />
            </div>
          </div>
          <span className={style.divider}></span>
          <div className={style.sub_menu_items}>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/users'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Users</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/salesman'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Salesman</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/branches'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Branches</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/my-company'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>My Company</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/policies-and-disclaimer'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Policies and Disclaimer</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/language'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Language</div>
            </NavLink>
          </div>
        </div>
      ) : openedMenu === 'reports' ? (
        <div className={style.sub_menu}>
          <div className={style.sub_menu_header_container}>
            <div className={style.sub_menu_header}>Reports</div>
            <div className={style.back_btn}>
              <img onClick={handleClickOnBackBtn} src={back_btn} alt='' />
            </div>
          </div>
          <span className={style.divider}></span>
          <div className={style.sub_menu_items}>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/order-summary'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Order Summary</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/finance-statements'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Financial Reports</div>
            </NavLink>
            <NavLink
              onClick={handleClickOnNavBtn}
              to='/business-reports'
              className={style.sub_menu_items_link}
              activeClassName={style.activePage}
            >
              <div>Business Reports</div>
            </NavLink>
          </div>
        </div>
      ) : (
        ''
      )}
    </div>
  );
};

// Menu.propTypes = {
//   setMenuOpen: PropTypes.func.isRequired,
// };

export default Menu;
