import React, { useState, useRef, useEffect } from 'react';
import PropTypes from 'prop-types';

import style from './SelectBox.module.css';

import arrow_icon from '../../../assets/images/arrow_icon.png';

const SelectBox = ({ items, inputRef, setValue, name }) => {
  const wrapperRef = useRef(null);
  const [showItems, setShowItems] = useState(false);
  const [activeItem, setActiveItem] = useState(items[0]);

  useEffect(() => {
    document.addEventListener('click', handleClickOutside, false);
    return () => {
      document.removeEventListener('click', handleClickOutside, false);
    };
  }, []);

  const handleClickOutside = (event) => {
    if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
      setShowItems(false);
    }
  };

  const onSelect = (item) => {
    setActiveItem(item);
    setValue(name, item.value, { shouldValidate: true });
    setShowItems(false);
  };

  const handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      setShowItems(true);
    }
    if (e.keyCode === 40 && activeItem.id !== items.length) {
      e.preventDefault();
      setActiveItem(items[activeItem.id]);
      setValue(name, items[activeItem.id].value, { shouldValidate: true });
    }
    if (e.keyCode === 38 && activeItem.id !== 1) {
      e.preventDefault();
      setActiveItem((activeItem) => items[activeItem.id - 2]);
      setValue(name, items[activeItem.id - 2].value, {
        shouldValidate: true,
      });
    }
  };

  return (
    <div className={style.box} ref={wrapperRef}>
      <div
        className={style.container}
        tabIndex='0'
        onClick={() => setShowItems((showItems) => !showItems)}
        onKeyDown={handleKeyPress}
      >
        <div className={style.selected_item}>
          {activeItem.label === items[0].label ? (
            <span style={{ color: 'var(--letter-blue)' }}>
              {activeItem.label}
            </span>
          ) : (
            activeItem.label
          )}
        </div>
        <div className={style.arrow}>
          <img
            className={showItems ? style.arrow_up : style.arrow_down}
            src={arrow_icon}
            alt=''
          />
        </div>
      </div>
      <div
        style={{ display: showItems ? 'block' : 'none' }}
        className={style.item_container}
      >
        {items &&
          items.map((item) => (
            <div
              key={item.id}
              className={
                activeItem.value === item.value
                  ? style.active_item + ' ' + style.item
                  : style.item
              }
              onClick={() => onSelect(item)}
            >
              {item.label}
            </div>
          ))}
      </div>

      <input type='hidden' name={name} ref={inputRef} />
    </div>
  );
};

SelectBox.propTypes = {
  items: PropTypes.array.isRequired,
  inputRef: PropTypes.oneOfType([
    PropTypes.func,
    PropTypes.shape({ current: PropTypes.any }),
  ]),
  setValue: PropTypes.func.isRequired,
};

export default SelectBox;
